import './index.css';
import React from 'react';
import Header from './components/Header';
import Main from './components/Main';
import Footer from './components/Footer';
import PopupWithForm from './components/PopupWithForm';

function App() {
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true);
  };

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(true);
  };

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true);
  };

  const closeAllPopups = () => {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
  };

  return (
    <div class="page">
      <Header />
      <Main
        onEditAvatarClick={handleEditAvatarClick}
        onEditProfileClick={handleEditProfileClick}
        onAddPlaceClick={handleAddPlaceClick}
        onClose={closeAllPopups}
      />

      <PopupWithForm name={'edit-profile'} title={'Edital perfil'}>
        <input
          type="text"
          name="name"
          id="input-name"
          placeholder="Nombre"
          minlength="2"
          maxlength="40"
          required
          class="popup__input popup__input_name"
        />
        <span class="popup__line" id="input__error-name"></span>
        <input
          type="text"
          name="occupation"
          id="occupation"
          placeholder="Acerca de mÃ­"
          minlength="2"
          maxlength="200"
          required
          class="popup__input popup__input_occupation"
        />
        <span class="popup__line" id="input__error-occupation"></span>
      </PopupWithForm>

      <section class="elements"></section>

      <Footer />
    </div>
  );
}

export default App;
