import React, { useState } from 'react';
import headerLogo from '../images/headerlogo.svg';
import { Link, useHistory } from 'react-router-dom';
import InfoToolTip from './InfoTooltip';
import * as auth from '../utils/auth';

export default function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const [open, setOpen] = useState(false);
  const [isRegistered, setIsRegistered] = useState(true);

  const history = useHistory();

  const handleSubmit = (event) => {
    event.preventDefault();
    if (email && password) {
      auth
        .register(email, password)
        .then((res) => {
          console.log(res, res._id);
          if (!res._id) {
            setIsRegistered(false);
          }
          setOpen(true);
          setTimeout(() => {
            history.push('/login');
          }, 2000);
        })
        .catch((error) => {
          console.log(error);
          setOpen(true);
          setIsRegistered(false);
        });
    }
  };

  const handleRegister = async () => {
    try {
      const response = await fetch(
        'https://tripleten.desarrollointerno.com/signup',
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(userData), // userData contiene los datos del formulario
        }
      );

      if (response.ok) {
        setIsRegistered(true); // Registro exitoso
      } else {
        setIsRegistered(false); // Fallo en el registro
      }
    } catch (error) {
      setIsRegistered(false); // En caso de error en la petición
    } finally {
      setOpen(true); // Mostrar el popup tras intentar el registro
    }
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <>
      <div className="register">
        <div className="register__header-wrapper">
          <div className="register__header">
            <img src={headerLogo} alt="encabezado" className="register__logo" />
            <Link to="login" className="register__header-register">
              Iniciar sesión
            </Link>
          </div>
          <div className="register__line"></div>
        </div>
        <h2 className="register__title">Regístrate</h2>
        <form onSubmit={handleSubmit} className="register__form">
          <input
            className="register__form-item"
            name="email"
            type="email"
            placeholder="Correo electrónico"
            required
            value={email}
            onChange={(event) => setEmail(event.target.value)}
          />
          <input
            className="register__form-item"
            name="password"
            type="password"
            placeholder="Contraseña"
            required
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
          <button
            onClick={handleRegister}
            type="submit"
            className="register__button"
          >
            Regístrate
          </button>
        </form>
        <Link className="register__login" to="login">
          ¿Ya eres miembro? Inicia sesión aquí
        </Link>
      </div>
      <InfoToolTip
        open={open}
        isRegistered={isRegistered}
        handleClose={handleClose}
      />
    </>
  );
}
