import React, { useContext, useState } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import api from '../utils/api';
import '../blocks/profile.css';
import Card from './Card';
import ImagePopup from './ImagePopup';

function Main({
  onEditAvatarClick,
  onEditProfileClick,
  onAddPlaceClick,
  onCardClick,
  onClose,
  selectedCard,
}) {
  /*  const [userName, setUserName] = useState('');
  const [userDescription, setUserDescription] = useState('');
  const [userAvatar, setUserAvatar] = useState(''); */
  const { currentUser } = useContext(CurrentUserContext);
  const [cards, setCards] = useState([]);

  /*   useEffect(() => {
    api
      .getUserInfo()
      .then((data) => {
        setUserName(data.name);
        setUserDescription(data.about);
        setUserAvatar(data.avatar);
      })
      .catch((error) => {
        console.error('Error', error);
      }); */

  api
    .getInitialCards()
    .then((res) => {
      setCards(res);
    })
    .catch((error) => {
      console.error('Error', error);
    });

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    api.likeCard(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
    });
  }

  return (
    <>
      <section className="profile">
        <div className="profile__picture-wrapper">
          <div className="profile__picture-overlay"></div>
          <img
            src={currentUser?.avatar}
            alt="foto de perfil"
            className="profile__picture"
          />
          <button
            className="profile__picture-button"
            onClick={onEditAvatarClick}
          ></button>
        </div>

        <div className="profile__container">
          <h1 className="profile__name">{currentUser?.name}</h1>
          <button
            type="button"
            className="profile__button"
            onClick={onEditProfileClick}
          ></button>
          <h2 className="profile__about-me">{currentUser?.about}</h2>
        </div>
        <button
          type="button"
          className="profile__add-button"
          onClick={onAddPlaceClick}
        ></button>
      </section>

      <section className="elements">
        {cards?.map((card, index) => {
          return (
            <Card
              key={index}
              card={card}
              imagePopup={selectedCard}
              onCardClick={onCardClick}
              onClose={onClose}
              onCardLike={handleCardLike}
            ></Card>
          );
        })}
      </section>

      {selectedCard && (
        <ImagePopup selectedCard={selectedCard} onClose={onClose} />
      )}
    </>
  );
}

export default Main;
