import React, { useState } from 'react';
import headerLogo from '../images/headerlogo.svg';
import InfoToolTip from './InfoTooltip';
import { Link, useHistory } from 'react-router-dom';
import * as auth from '../utils/auth';

export default function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const [open, setOpen] = useState(false);
  const [isRegistered, setIsRegistered] = useState(true);

  const history = useHistory();

  const handleSubmit = (event) => {
    event.preventDefault();
    if (email && password) {
      auth
        .register(email, password)
        .then((res) => {
          console.log(res, res._id);
          if (!res._id) {
            setIsRegistered(false);
          }
          setOpen(true);
          setTimeout(() => {
            history.push('/login');
          }, 2000);
        })
        .catch((error) => {
          console.log(error);
          setOpen(true);
          setIsRegistered(false);
        });
    }
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <>
      <div className="register">
        <div class="register__header-wrapper">
          <div className="register__header">
            <img src={headerLogo} alt="encabezado" className="register__logo" />
            <Link to="login" className="register__header-register">
              Iniciar sesión
            </Link>
          </div>
          <div className="register__line"></div>
        </div>
        <h2 className="register__title">Regístrate</h2>
        <form onSubmit={handleSubmit} className="register__form">
          <input
            className="register__form-item"
            name="email"
            type="email"
            placeholder="Correo electrónico"
            required
            /*  value={email} */
            onChange={(event) => setEmail(event.target.value)}
          />
          <input
            className="register__form-item"
            name="password"
            type="password"
            placeholder="Contraseña"
            required
            /*  value={password} */
            /*  onChange={(e) => setPassword(e.target.value)} */
          />
          <button type="submit" className="register__button">
            Inicia Sesión
          </button>
        </form>
        <Link className="register__login" to="login">
          ¿Ya eres miembro? Inicia sesión aquí
        </Link>
      </div>
      <InfoToolTip
        open={open}
        isRegistered={isRegistered}
        handleClose={handleClose}
      />
    </>
  );
}
