import '../index.css';
import React from 'react';
import { useState, useEffect } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import api from '../utils/api';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';

function App() {
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [cards, setCards] = useState([]);
  const [cardToDelete, setCardToDelete] = useState({});
  const [currentUser, setCurrentUser] = useState();

  function handleCardDelete(card) {
    setCardToDelete(card);
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    api.likeCard(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
    });
  }

  useEffect(() => {
    api.getUserInfo().then((data) => setCurrentUser(data));
  }, []);

  const handleUpdateUser = ({ name, about }) => {
    api
      .updateUser({ name, about })
      .then((newUserData) => {
        setCurrentUser(newUserData);
        setIsEditProfilePopupOpen(false);
      })
      .catch((error) => {
        console.error('Error', error);
      });
  };

  function handleUpdateAvatar(avatar) {
    api.updateAvatar(avatar).then((updateUser) => {
      setCurrentUser(updateUser);
      setIsEditAvatarPopupOpen(false);
    });
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true);
  };

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(true);
  };

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true);
  };

  const closeAllPopups = () => {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null);
  };

  useEffect(() => {
    api.getInitialCards().then((res) => {
      setCards(res);
    });
  }, []);

  return (
    <div className="page">
      <CurrentUserContext.Provider value={{ currentUser }}>
        <Header />
        <Main
          onEditAvatarClick={handleEditAvatarClick}
          onEditProfileClick={handleEditProfileClick}
          onAddPlaceClick={handleAddPlaceClick}
          onCardClick={handleCardClick}
          onClose={closeAllPopups}
          cards={cards}
          selectedCard={selectedCard}
          onCardLike={handleCardLike}
          onCardDelete={handleCardDelete}
        />

        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
        />

        {/*    <PopupWithForm
          name={'edit-profile'}
          title={'Edital perfil'}
          buttonTitle={'Guardar'}
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
        >
          <>
            <input
              type="text"
              name="name"
              id="input-name"
              placeholder="Nombre"
              minLength="2"
              maxLength="40"
              required
              className="popup__input popup__input_name"
            />
            <span className="popup__line" id="input__error-name"></span>
            <input
              type="text"
              name="occupation"
              id="occupation"
              placeholder="Acerca de mí"
              minLength="2"
              maxLength="200"
              required
              className="popup__input popup__input_occupation"
            />
            <span className="popup__line" id="input__error-occupation"></span>
          </>
        </PopupWithForm> */}

        {/*  <PopupWithForm
          name={'add-button'}
          title={'Nuevo lugar'}
          buttonTitle={'Crear'}
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
        >
          <>
            <input
              type="text"
              name="title"
              id="title"
              placeholder="Título"
              minLength="2"
              maxLength="30"
              required
              className="title-input-error popup__input popup__input_title"
            />
            <span className="popup__line" id="input__error-name"></span>
            <input
              type="url"
              name="link"
              id="link"
              placeholder="Enlace a la imágen"
              minLength="7"
              required
              className="popup__input popup__input_link"
            />
            <span className="popup__line" id="input__error-link"></span>
          </>
        </PopupWithForm> */}

        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        />

        {/*  <PopupWithForm
          name={'profile-button'}
          title={'Cambiar foto de perfil'}
          buttonTitle={'Guardar'}
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
        >
          <div className="popup__wrapper">
            <input
              type="url"
              name="picture"
              id="popup__avatar-input"
              placeholder="Imágen de perfil"
              minLength="7"
              required
              className="popup__input popup__input_link"
            />
            <span className="popup__line" id="input__error-picture"></span>
          </div>
        </PopupWithForm> */}
        <AddPlacePopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
        />

        <Footer />
      </CurrentUserContext.Provider>
    </div>
  );
}

export default App;
