import '../index.css';
import React from 'react';
import { useState, useEffect } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import api from '../utils/api';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import EditProfilePopup from './EditProfilePopup';

function App() {
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [currentUser, setCurrentUser] = useState();

  useEffect(() => {
    api
      .getUserInfo()
      .then((data) => {
        setCurrentUser(data);
      }, [])
      .catch((error) => {
        console.error('Error', error);
      });

    const handleUpdateUser = ({ name, about }) => {
      api
        .updateUser({ name, about })
        .then((newDataUser) => {
          setCurrentUser(newDataUser);
          setIsEditProfilePopupOpen(false);
        })
        .catch((error) => {
          console.error('Error', error);
        });
    };
  });

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true);
  };

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(true);
  };

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true);
  };

  const closeAllPopups = () => {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null);
  };

  return (
    <div className="page">
      <CurrentUserContext.Provider value={{ currentUser }}>
        <Header />
        <Main
          onEditAvatarClick={handleEditAvatarClick}
          onEditProfileClick={handleEditProfileClick}
          onAddPlaceClick={handleAddPlaceClick}
          onCardClick={handleCardClick}
          onClose={closeAllPopups}
          selectedCard={selectedCard}
        />

        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
        />

        {/*    <PopupWithForm
          name={'edit-profile'}
          title={'Edital perfil'}
          buttonTitle={'Guardar'}
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
        >
          <>
            <input
              type="text"
              name="name"
              id="input-name"
              placeholder="Nombre"
              minLength="2"
              maxLength="40"
              required
              className="popup__input popup__input_name"
            />
            <span className="popup__line" id="input__error-name"></span>
            <input
              type="text"
              name="occupation"
              id="occupation"
              placeholder="Acerca de mí"
              minLength="2"
              maxLength="200"
              required
              className="popup__input popup__input_occupation"
            />
            <span className="popup__line" id="input__error-occupation"></span>
          </>
        </PopupWithForm> */}

        <PopupWithForm
          name={'add-button'}
          title={'Nuevo lugar'}
          buttonTitle={'Crear'}
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
        >
          <>
            <input
              type="text"
              name="title"
              id="title"
              placeholder="Título"
              minLength="2"
              maxLength="30"
              required
              className="title-input-error popup__input popup__input_title"
            />
            <span className="popup__line" id="input__error-name"></span>
            <input
              type="url"
              name="link"
              id="link"
              placeholder="Enlace a la imágen"
              minLength="7"
              required
              className="popup__input popup__input_link"
            />
            <span className="popup__line" id="input__error-link"></span>
          </>
        </PopupWithForm>

        <PopupWithForm
          name={'profile-button'}
          title={'Cambiar foto de perfil'}
          buttonTitle={'Guardar'}
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
        >
          <div className="popup__wrapper">
            <input
              type="url"
              name="picture"
              id="popup__avatar-input"
              placeholder="Imágen de perfil"
              minLength="7"
              required
              className="popup__input popup__input_link"
            />
            <span className="popup__line" id="input__error-picture"></span>
          </div>
        </PopupWithForm>

        <Footer />
      </CurrentUserContext.Provider>
    </div>
  );
}

export default App;
